class Hamburger{static SIZES={SMALL:{price:50,calories:20},LARGE:{price:100,calories:40}};static STUFFINGS={CHEESE:{price:10,calories:20},SALAD:{price:20,calories:5},POTATO:{price:15,calories:10}};static TOPPINGS={MAYO:{price:20,calories:5},SAUCE:{price:15,calories:0}};constructor(e,t){this.size=e,this.stuffing=t,this.toppings=[]}addTopping(e){this.toppings.push(e)}calculatePrice(){return this.size.price+this.stuffing.price+this.toppings.reduce((e,t)=>e+t.price,0)}calculateCalories(){return this.size.calories+this.stuffing.calories+this.toppings.reduce((e,t)=>e+t.calories,0)}}function handleFormSubmit(e){e.preventDefault();var e=document.getElementById("size").value.toUpperCase(),t=document.getElementById("stuffing").value.toUpperCase(),i=getSelectedToppings();const c=new Hamburger(Hamburger.SIZES[e],Hamburger.STUFFINGS[t]);i.forEach(e=>c.addTopping(Hamburger.TOPPINGS[e])),displayHamburgerInfo(c)}function getSelectedToppings(){var e=[];return document.getElementById("mayo").checked&&e.push("MAYO"),document.getElementById("sauce").checked&&e.push("SAUCE"),e}function displayHamburgerInfo(e){document.getElementById("calories").textContent=e.calculateCalories(),document.getElementById("price").textContent=e.calculatePrice()}function updateGithubLink(){var[e,t]=window.location.href.split("/").slice(2,4),e=`https://github.com/${e}/`+t;document.getElementById("github-link").href=e}document.addEventListener("DOMContentLoaded",function(){var e=document.getElementById("hamburgerForm");document.getElementById("calories"),document.getElementById("price");e.addEventListener("submit",handleFormSubmit),updateGithubLink()});